mapper.insert:
INSERT INTO manager
       (id, pw, name, phone, email) VALUES ( #{id}, #{pw}, #{name}, #{phone}, #{email}
       NULL )

mapper.select:
SELECT * FROM ( SELECT m.*, ROWNUM rnum FROM (
       SELECT no, id, name, phone, email, reg_date FROM manager  WHERE ${f} LIKE '%' || #{q} || '%'  ORDER BY no ) m
       WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

SELECT no, id, name, phone, email
FROM manager
WHERE no = #{no}

mapper.update:
UPDATE manager

    pw = #{data.pw},
    name = #{data.name},
    phone = #{data.phone},
    email = #{data.email}

WHERE no = #{data.no}

mapper.select:
SELECT id
FROM manager
WHERE phone = #{phone}

mapper.update:
UPDATE manager
SET pw = #{pw}
WHERE id = #{id}
